	<script>
	jQuery.noConflict();
	function showMailBoxbds(){				
		jQuery('#showboxbds').bPopup();
		var tieude = trim(jQuery(".title_details").text());
		var nguoinhan = trim(jQuery("#emailnguoilienhe").text());
		jQuery('input[name="subjectbds"]').val("Liên hệ: "+tieude);
		if(nguoinhan != ''){
			jQuery('input[name="mailtobds"]').val(nguoinhan);
		}
		jQuery("#sendmailbds").click(function(){
			var mailfrom 	= 	jQuery("#mailfrombds").val();
			var mailto 		= 	jQuery("#mailtobds").val();
			var subject 	=	jQuery("#subjectbds").val();
			var content 	=	jQuery("#contentbds").val();
			var input_data = '&mailfrombds='+mailfrom+'&mailtobds='+mailto+'&subjectbds='+subject+'&contentbds='+content;
			jQuery.ajax({
					   type: "POST",
					   url:  "http://thietkewebbatdongsan.com/release/webmlbds1/modules/mod_socialbds/tmpl/email/sendmail.php",
					   data: input_data,
					   success: function(msg){
						   jQuery('.loading-img').remove();
						   jQuery('<div class="message">').html(msg).appendTo('div#result').hide().fadeIn('slow');
						   jQuery("#mailfrombds").val("Email của bạn");
						   jQuery("#mailtobds").val("Email người nhận");
						   jQuery("#subjectbds").val("Tiêu đề thư");
						   jQuery("#contentbds").val("Nội dung thư");
					   }
					});
				return false;
		    });
	}
	
	
	</script>
